// /api/create-mobile-banking-source.js

const omise = require('omise')({
    'secretKey': process.env.OMISE_SECRET_KEY,
    'omiseVersion': '2019-05-29'
});

export default async function handler(req, res) {
    if (req.method !== 'POST') {
        res.setHeader('Allow', 'POST');
        return res.status(405).json({ message: 'Method Not Allowed' });
    }

    try {
        const { type, amount, currency, return_uri } = req.body;

        if (!type || !amount || !currency || !return_uri) {
            return res.status(400).json({ message: 'Missing required fields: type, amount, currency, or return_uri.' });
        }

        const source = await omise.sources.create({
            type,
            amount,
            currency,
            return_uri
        });

        // Immediately create a charge for this source
        await omise.charges.create({
            source: source.id,
            amount: source.amount,
            currency: source.currency,
            description: 'Order from Ginger Bros Website (Mobile Banking)',
            return_uri // Also include return_uri in the charge
        });
        
        res.status(200).json(source);

    } catch (error) {
        console.error('Omise mobile banking source error:', error);
        res.status(500).json({ message: error.message || 'An error occurred during payment processing.' });
    }
}